import React from "react";
import ReactDOM from "react-dom";
import './index.css';
const root = document.getElementById("root");

/* Todo List

* Get out of bed, Wed Sep 13 2017
* Brush teeth, Thu Sep 14 2017
* Eat breakfast, Fri Sep 15 2017 */

const todo = [
             {
              task: " Get out of bed",
              date : " Wed Sep 13 2017"
            },
            {
              task: "Brush teeth",
              date : " Thu Sep 14 2017"
            },

            {
              task: "Eat breakfast",
              date : "Fri Sep 15 2017 "
            } ];



function MyTodo (props) {
  const list = props.list;
  const myToDoList = list.map((tasks) => {

  return (<li>{tasks.task}, {tasks.date}</li>)
  })
return (<ul>{myToDoList}</ul>)
}


class Footer extends React.Component {
  render(){
    return (<h3>This is your list</h3>)
  }
}




ReactDOM.render(
<div>
 <h1>Todo List</h1>
 <MyTodo list={todo} />
 <Footer />
</div>,root);
