function HelloUser(props) {
  console.log(props);
  let result;
  if (props.age < 18) result = "you can't drive";
  else result = "go ahead";
  return (
    <h3>
      hello {props.name}, {result}
    </h3>
  );
}
class HelloUserClass extends React.Component {
  render() {
    let result;
    if (this.props.age < 18) result = "you can't drive";
    else result = "go ahead";
    return (
      <h3>
        goodbye {this.props.name}, {result}
      </h3>
    );
  }
}
class SayHiToMultiplePeople extends React.Component {
  render() {
    const multiplePeople = this.props.people;
    const result = multiplePeople.map(person => {
      return <HelloUser name={person.name} age={person.age} />;
    });
    return <ul>{result}</ul>;
  }
}
