// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister(); */

/**
welcome yo my app // header component
  * dish number 1
  * dish number 2 // list component
  * dish x x
enjoy the menu // footer component
dishes = [
  dish number 1,
  dish number 2,
  ....,
  ....
]
*/
import React from "react";
import ReactDOM from "react-dom";
import './index.css';
const arrayOfDishes = ["dish 1", "dish 2", "dish 3"];
function Header() {
  return <h1>welcome to my app</h1>;
}
function DishList(props) {
  const dishesCompnents = props.dishes.map(dish => {
    return <li>{dish}</li>;
  });
  return <ul>{dishesCompnents}</ul>;
}
class Footer extends React.Component {
  render() {
    return <h3>enjoy the menu </h3>;
  }
}
class App extends React.Component {
  render() {
    return (
      <div>
        <Header />
        <DishList dishes={arrayOfDishes} />
        <Footer />
      </div>
    );
  }
}
ReactDOM.render(<App />, document.getElementById("root"));
