import React from "react";
const list = [
  {
    id: 1,
    description: "Get out of bed",
    completed: false
  },
  {
    id: 2,
    description: "Brush teeth",
    completed: false
  },
  {
    id: 3,
    description: "Eat breakfast",
    completed: false
  }
];

const newList = [
  {
    id: 4,
    description: "Go for a walk"
  },

  {
    id: 5,
    description: "Finish homework "
  }
]

function RenderList(props) {
  const list = props.todos.map(item => {
    return <li key={item.id}>{item.description}  <button onClick={props.deleteTodo}>Delete</button></li>;
  });
  return list;
}

class TodoList extends React.Component {
  state = {
    listOfTodos: []
  };

  addTodo = this.addTodo.bind(this);
  removeTodo = this.removeTodo.bind(this);

  addTodo() {
    this.setState({
      listOfTodos: [
        ...this.state.listOfTodos,
        newList[Math.floor(Math.random() * newList.length)]
      ]
    });
  }


  removeTodo(index){
   const removedIndex = this.state.listOfTodos.map((item)=>{
      if(item.id !== index) {
        return item
      }
    })
    this.setState({listOfTodos:[ ...this.state.listOfTodos, removedIndex ] })
    console.log(this.state.listOfTodos)
  }

  render() {
    return (
      <div>
        <button onClick={this.addTodo}> Add to do</button>
        <ul>
          <RenderList todos={list} deleteTodo={this.removeTodo}/>
          
          <RenderList todos={this.state.listOfTodos} deleteTodo={() => this.removeTodo(this.state.listOfTodos.id)}/>
        </ul>
      </div>
    );
  }
}

export default TodoList;
