import React from "react";

function RenderList(props) {
  const isComplete = props.todos.completed;
return  <li
  style={{textDecoration: isComplete ? "line-through" : "none"}} >
    
    {props.todos.description} 

    <input type='checkbox' onChange={props.handelCheckbox}></input>
     <button onClick={props.deleteTodo}> Delete</button>
  </li>

     
 
}

class TodoList extends React.Component {
  state = {
    listOfTodos: [{
      id: 1,
      description: "Get out of bed",
      completed: false
    },
    {
      id: 2,
      description: "Brush teeth",
      completed: false
    },
    {
      id: 3,
      description: "Eat breakfast",
      completed: false
    }]
  };

  addTodo = this.addTodo.bind(this);
  removeTodo = this.removeTodo.bind(this);
  handelCheckbox = this.handelCheckbox.bind(this);

  addTodo() {
    this.setState({
      listOfTodos: [
        ...this.state.listOfTodos,
        {
          id: 4,
          description: "Go for a walk",
          completed: true
        }
      ]
    });
  }

  handelCheckbox(Index){

   this.setState({listOfTodos: this.state.listOfTodos.map((task) => {

  
   })                    })

  
  }





  removeTodo(Index){
    this.setState({
      listOfTodos: this.state.listOfTodos.filter((item) => {
        console.log(item.id, Index)
        return item.id !== Index

      } )
      
    });
    console.log(this.state.listOfTodos)
  }

  render() {
    return (
      <div>
      <h1>TodoList</h1>
        <ul>
          {this.state.listOfTodos.map((task) =>{
         
         return   <RenderList todos= {task} deleteTodo={()=> this.removeTodo(task.id) }  handelCheckbox = {()=>this.handelCheckbox(task.id)}/>
          })}
             
<button onClick={this.addTodo}> Add to do</button>
        </ul>
      </div>
    );
  }
}

export default TodoList;
