import React from "react";
const list = [
  {
    id: 1,
    description: "Get out of bed"
  },
  {
    id: 2,
    description: "Brush teeth"
  },
  {
    id: 3,
    description: "Eat breakfast"
  },

  {
    id: 4,
    description: "Go for a walk"
  },

  {
    id: 5,
    description: "Finish homework "
  }
];

class TodoList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      todoList: [],

      remove: "off",

      display: "off",

      text: "on"
    };

    this.onAdd = this.onAdd.bind(this);
    this.onDelete = this.onDelete.bind(this);
    this.handleDelete = this.handleDelete.bind(this);
  }

  onAdd() {
    this.setState({ display: "on" });
  }
  
   checkBox(text) {
    this.setState({ text: "on" });

   }

  onDelete(task) {
    this.setState({ remove: "on" });

    this.state.todoList.map(item => {
      const index = this.state.todoList.indexOf(item);
      if (task === item.id && this.state.remove === "on") {
        this.state.todoList.splice(index, 1);
      }
    });

   
  }

  render() {
    const todoList = this.state.todoList;
    let displayList;

    if (this.state.display === "on" && list.length !== 0) {
      todoList.push(list[list.length - 1]);
      list.splice([list.length - 1]);
    } else if (this.state.display === "on" && list.length !== 0) {
      console.log("all renderd");
    }

    displayList = todoList.map(task => {
      return (
        <li key={task.id}>
          {task.description}{" "} <input type="checkbox" value="unchecked" />
          <button onClick={() => this.onDelete(task.id)}> Delete </button>
        </li>
      );
    });

    if (this.state.todoList.length === 0) {
      displayList = <h2> NO items </h2>
    }

    return (
      <div>
        <h1>TodoList</h1>
        <ul>{displayList} </ul>
        <button onClick={this.onAdd}>Add todo</button>
      </div>
    );
  }
}

export default TodoList;
