import React from "react";
const todoList = [
  {
    id: 1,
    description: "Get out of bed",
    display: "off"
  },
  {
    id: 2,
    description: "Brush teeth",
    display: "off"
  },
  {
    id: 3,
    description: "Eat breakfast",
    display: "off"
  }
]
class TodoList extends React.Component {

 
  constructor(props) {
    super(props);
  


this.state = {
  todoList : [
    {
      id: 1,
      description: "Get out of bed",
      display: "off"
    },
    {
      id: 2,
      description: "Brush teeth",
      display: "off"
    },
    {
      id: 3,
      description: "Eat breakfast",
      display: "off"
    }
  ],
  
  
  value :2,display :"off"    }
  


  this.onChange = this.onChange.bind(this);
}



onChange(){
  
  this.state.todoList.map((item)=>{
    console.log(this.state.todoList)
    return item.display = "on"
  })

  this.setState({value:8})

}


render(){
  console.log(this.state.todoList)
  const todoList = this.state.todoList;

 const showTodo =  todoList.map((task)=>{
    if(task.display === "on" && this.state.value === 8){
      return <li>{task.description}</li>
    } 
  })
  

return (

  <div>
       
<ul>{showTodo}</ul>
  <button onClick={this.onChange}>on change</button>
  </div>
  
)}

}









export default TodoList;
