import React from "react";

function RenderList(props) {
  const isComplete = props.todos.completed;
  return (
    <li
      id="lists"
      style={{ textDecoration: isComplete ? "line-through" : "none" }}
    >
      {props.todos.description} || {props.todos.deadline}
      <input type="checkbox" onChange={props.handelCheckbox}></input>
      <button onClick={props.deleteTodo}> Delete</button>
      <button onClick={props.editTodo}>Edit </button>
     
    </li>
  );
}

class TodoList extends React.Component {
  state = {
    listOfTodos: [],
    todoName: "",
    deadlineDate: "",
    updateInput:"",
  };

  addTodo = this.addTodo.bind(this);
  removeTodo = this.removeTodo.bind(this);
  handelCheckbox = this.handelCheckbox.bind(this);
  update=this.update.bind(this);

  feachTodoArray() {
    return fetch(
      "https://gist.githubusercontent.com/benna100/391eee7a119b50bd2c5960ab51622532/raw"
    ).then((data) => data.json());
  }

  addTodo() {
    this.feachTodoArray().then((response) => {
      const random = response[Math.floor(Math.random() * response.length)];

      this.setState({
        listOfTodos: [...this.state.listOfTodos, random],
      });
    });
  }

  addNewTodo(todoName, deadlineDate) {
    this.setState({
      listOfTodos: [
        ...this.state.listOfTodos,
        { description: todoName, completed: false, deadline: deadlineDate },
      ],
    });
  }
  update(Input,Index) {
    this.setState({
      listOfTodos:this.state.listOfTodos.map((tasks,index)=> {
        if(Index ===  index) {
          return  [ ...this.state.listOfTodos,
              {  description:Input, completed: false },
          ]
          
        
        } 
        
      })
      
      
     
        
      });
  
  }

  editTodo(Index) {
    this.setState({
      listOfTodos: this.state.listOfTodos.map((task, index) => {
        if (Index === index) {
          return {
            ...task,
            description: (<form onSubmit={(event) => {
                event.preventDefault();
                this.update(this.state.updateInput,index);
                this.setState({ updateInput: "" });
                }}>
              <input type="text" placeholder="update todo" 
               
               onChange={(event) => this.setState({ updateInput: event.target.value})} value={this.state.updateInput} />
               <button type="submit">update todo</button>
          </form>
            )
          }
        } 
        
        else {
          return task;
        }
      }),
    });
  }


  updateTodo(Index) {
  
  }

  handelCheckbox(Index) {
    this.setState({
      listOfTodos: this.state.listOfTodos.map((task, index) => {
        if (Index === index) {
          return { ...task, completed: !task.completed };
        } else {
          return task;
        }
      }),
    });
  }

  removeTodo(Index) {
    this.setState({
      listOfTodos: this.state.listOfTodos.filter((item, index) => {
        return index !== Index;
      }),
    });
  }

  render() {
    return (
      <div id="display">
        <h1>TodoList</h1>

        <ul>
          {this.state.listOfTodos.map((task, index) => {
            return (
              <RenderList
                todos={task}
                key={index}
                deleteTodo={() => this.removeTodo(index)}
                handelCheckbox={() => this.handelCheckbox(index)}
                editTodo={() => this.editTodo(index)}
              />
            );
          })}

          <button onClick={this.addTodo}> Add to do</button>

          <form
            onSubmit={(event) => {
              event.preventDefault();
              this.addNewTodo(this.state.todoName, this.state.deadlineDate);
              this.setState({ todoName: "", deadlineDate: "" });
            }}
          >
            Todo description :{" "}
            <input
              type="text"
              placeholder="New Todo name"
              value={this.state.todoName}
              onChange={(event) => {
                console.log(event.target.value)
                this.setState({
                  todoName: event.target.value,
                });
              }}
            />
            Deadline :{" "}
            <input
              type="date"
              placeholder="New Deadline Date"
              value={this.state.deadlineDate}
              onChange={(event) => {
    
                this.setState({
                  deadlineDate: event.target.value,
                });
              }}
            />
            <button type="submit">Add new todo</button>
          </form>
        </ul>
      </div>
    );
  }
}

export default TodoList;
