import React from "react";

function RenderList(props) {
  const isComplete = props.todos.completed;
  return (
    <li style={{ textDecoration: isComplete ? "line-through" : "none" }}>
      {props.todos.description}

      <input type="checkbox" onChange={props.handelCheckbox}></input>
      <button onClick={props.deleteTodo}> Delete</button>
    </li>
  );
}

class TodoList extends React.Component {
  state = {
    listOfTodos: [],
  };

  addTodo = this.addTodo.bind(this);
  removeTodo = this.removeTodo.bind(this);
  handelCheckbox = this.handelCheckbox.bind(this);

  feachTodoArray() {
    return fetch(
      "https://gist.githubusercontent.com/benna100/391eee7a119b50bd2c5960ab51622532/raw"
    ).then((data) => data.json());
  }

  addTodo() {
    this.feachTodoArray().then((response) => {
     const random =  response[Math.floor(Math.random() * response.length)];

      
     
       this.setState({
          listOfTodos: 
          
          this.state.listOfTodos.map((item) => {
            if (item !== random) {

              return  [...this.state.listOfTodos, random]
            } else {
              return item
            }
          })
           
         
        })
      
      
    });
  }

  handelCheckbox(Index) {
    this.setState({
      listOfTodos: this.state.listOfTodos.map((task) => {
        if (Index === task.id) {
          return { ...task, completed: !task.completed };
        } else {
          return task;
        }
      }),
    });
  }

  removeTodo(Index) {
    this.setState({
      listOfTodos: this.state.listOfTodos.filter((item) => {
        return item.id !== Index;
      }),
    });
  }

  render() {
    return (
      <div>
        <h1>TodoList</h1>
        <ul>
          {this.state.listOfTodos.map((task, index) => {
            return (
              <RenderList
                todos={task}
                key={index}
                deleteTodo={() => this.removeTodo(task.id)}
                handelCheckbox={() => this.handelCheckbox(task.id)}
              />
            );
          })}

          <button onClick={this.addTodo}> Add to do</button>
        </ul>
      </div>
    );
  }
}

export default TodoList;
