class RenderTodo extends React.Component{

  state = {

    todoList : [ 
    {
      "id": 1,
      "description": "Get out of bed"
    },
    {
      "id": 2,
      "description": "Brush teeth"
    },
    {
      "id": 3,
      "description": "Eat breakfast"
    }
  ]}

randomTodo(){

  const myToDoList = this.state.todoList.map(tasks => {

    return <li key={tasks.id}>{tasks.description}</li>;
      });

     return <ul>
      { myToDoList[Math.floor(Math.random() * myToDoList.length)] } 
         </ul>;

  }

render(){
  const {isShow} = this.state;


     return <div> 

   {!isShow ? <Counter />:<p>nothing to show</p>}

    <h1>Todolist</h1>



  </div>



}

}


















