import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import Counter from "./count";
const root = document.getElementById("root");

const  todoList = [
  {
    id: 1,
    description: "Get out of bed"
  },
  {
    id: 2,
    description: "Brush teeth"
  },
  {
    id: 3,
    description: "Eat breakfast"
  }
]

class RenderTodo extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      todoList: [
        {
          id: 1,
          description: "Get out of bed"
        },
        {
          id: 2,
          description: "Brush teeth"
        },
        {
          id: 3,
          description: "Eat breakfast"
        }
      ]
    };
    this.randomTodo = this.randomTodo.bind(this);
  }
  randomTodo() {}

  render() {
    const { isShow } = this.state;
    const { todoList } = this.state;

    const myToDoList = todoList.map(tasks => {
      return <li key={tasks.id}>{tasks.description}</li>;
    });

    return (
      <div>
        {!isShow ? <Counter /> : <p>nothing to show</p>}

        <h1>Todolist</h1>

        <ul>{myToDoList[Math.floor(Math.random() * myToDoList.length)]}</ul>

        <button> Add to do</button>
      </div>
    );
  }
}

class App extends React.Component {

  render(){
    return (

      <RenderTodo />
    )
  }
}

function MyTodo(props) {
  const list = props.list;
 ///const myToDoList = list.map(tasks => {
   // return <li key={tasks.id}>{tasks.description}</li>;
  //});

  const myToDoList = list.map(tasks => {
     return <li>{tasks.description}</li>
   });

  return <ul>

   { myToDoList[Math.floor(Math.random() * myToDoList.length)] } 

  </ul>;
}



