import React from "react";

class TodoList extends React.Component {

    constructor(props) {
      super(props);
      this.state = {
        todoList : [
            {
              id: 1,
              description: "Get out of bed"
            },
            {
              id: 2,
              description: "Brush teeth"
            },
            {
              id: 3,
              description: "Eat breakfast"
            }
          ],

          display : "none"
          
      }

      this.displayTodo = this.displayTodo.bind(this);
      this.OnDelet = this.OnDelet.bind(this);
    } 


displayTodo () {
    const displayState = this.state.display
    this.setState({ display:'true'});
    console.log(displayState)
}

OnDelet() {
    const todo = this.state.todoList
    console.log('delet runing')
}

    

render(){
    const displayState = this.state.display
    console.log(displayState);
    const todo = this.state.todoList
    
    return <div>
 { displayState === 'none' ?  <p>nothing to show</p>   : 
 todo.map((task)=>{
    return <li key={task.id}>{task.description}  <input type="checkbox" value="unchecked" />         <button onClick={this.OnDelet}>Delet </button> </li> 
 
    
})
}
<button onClick={this.displayTodo}> click to add</button>
    </div>


}


}




export default TodoList;
