import React from "react";
const list = [
  {
    id: 1,
    description: "Get out of bed",
    display: "off"
  },
  {
    id: 2,
    description: "Brush teeth",
    display: "off"
  },
  {
    id: 3,
    description: "Eat breakfast",
    display: "off"
  },

  {
    id: 4,
    description: "Go for a walk",
    display: "off"
  },

  {
    id: 5,
    description: "Finish homework ",
    display: "off"
  }
];

class TodoList extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      todoList: [],

      value: 2,

      display: "off"
    };

    this.onChange = this.onChange.bind(this);
  }

  onChange() {
    this.setState({ display: "on" });
  }

  render() {
    const todoList = this.state.todoList;

    if (this.state.display === "on" && list.length !== 0) {
      //todoList.push(list[Math.floor(Math.random()*list.length)])
      todoList.push(list[list.length - 1]);
      list.splice([list.length - 1]);
    } else if (this.state.display === "on" && list.length !== 0) {
      console.log("all renderd");
    }

    const displayList = todoList.map(task => {
      return <li key={task.id}>{task.description}</li>;
    });

    return (
      <div>
        <ul>{displayList}</ul>
        <button onClick={this.onChange}>Add todo</button>
      </div>
    );
  }
}

export default TodoList;
