import React from "react";
const query = "Mekdes1";
class UsersLoding extends React.Component {
  state = { list: [] };

  componentDidMount() {
    fetch(`https://api.github.com/search/users?q=${query}`)
      .then((data) => data.json())
      .then((response) => {
        const items = response.items;

        this.setState({
          list: items.map((item) => {
            console.log(item);
          }),
        });
      });

    console.log(this.state.list);
  }

  render() {
    return <h1>{this.state.list}</h1>;
  }
}

export default UsersLoding;
