import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import FindUser from "./FindUser";
import context from "./context";

const ContextProvider = context.Provider;
function Container(props) {
  return <div className="container">{props.children}</div>;
}
class App extends React.Component {
  state = { userName: "", usersList: [], isLoading: false };
  handelUserSearch = this.handelUserSearch.bind(this);

  handelUserSearch(e) {
    const value = e.target.value;
    this.setState({ userName: value });

    const URL = `https://api.github.com/search/users?q=${value}`;
    fetch(URL)
      .then((data) => data.json())
      .then((response) => {
        const items = response.items;

        this.setState({
          usersList: items.map((item) => {
            return item;
          }),
          isLoading: true,
        });
      })
      .finally(() => {
        this.setState({ isLoading: false });
      });
  }

  render() {
    const searchedUser = this.state.usersList.map((item) => (
      <li key={item.id}>{item.login}</li>
    ));

    const status = this.state.isLoading
      ? "Loading . . ."
      : this.state.usersList.length === 0
      ? "No results"
      : "";

    const contextValue = {
      handelUserSearch: this.handelUserSearch,
    };

    return (
      <Container>
        <ContextProvider value={contextValue}>
          <h1>Github User Search</h1>
          <FindUser searchIs={this.handelUserSearch} />
          <ul>
            {searchedUser}
            <p>{status}</p>
          </ul>
        </ContextProvider>
      </Container>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
