import React from "react";
const query = "benna1002";
class UsersLoding extends React.Component {
  state = { list: [], fetchStatus: "Loading. . ." };

  componentDidMount() {
    fetch(`https://api.github.com/search/users?q=${query}`)
      .then((data) => data.json())
      .then((response) => {
        const items = response.items;

        this.setState({
          list: items.map((item) => {
            return item;
          }),
          fetchStatus: "done",
        });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  render() {
    console.log(this.state.fetchStatus);
    const searchedUser = this.state.list.map((item) => (
      <li key={item.id}>{item.login}</li>
    ));
    console.log(searchedUser);
    return (
      <ul>
        {searchedUser}
        <h1>{this.state.fetchStatus}</h1>
      </ul>
    );
  }
}

export default UsersLoding;
