import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import FindUser from "./FindUser";
import context from "./context";

const ContextProvider = context.Provider;
function Container(props) {
  return <div className="container">{props.children}</div>;
}
class App extends React.Component {
  state = { userName: "", usersList: [], requestStatus: "Loading. . ." };
  handelUserSearch = this.handelUserSearch.bind(this);

  fecthUser(query) {
    const URL = `https://api.github.com/search/users?q=${query}`;
    fetch(URL)
      .then((data) => data.json())
      .then((response) => {
        const items = response.items;

        this.setState({
          usersList: items.map((item) => {
            return item;
          }),
          requestStatus: "done",
        });
      })
      .catch((error) => {
        console.log(error);
        this.setState({ requestStatus: "Error" });
      });
  }

  handelUserSearch(e) {
    const value = e.target.value;
    console.log(value);
    this.setState({ userName: value });
    this.fecthUser(value);
  }

  render() {
    const searchedUser = this.state.usersList.map((item) => (
      <li key={item.id}>{item.login}</li>
    ));

    const status =
      this.state.requestStatus === "done" ? "" : this.state.requestStatus;

    const contextValue = {
      handelUserSearch: this.handelUserSearch,
    };

    return (
      <Container>
        <ContextProvider value={contextValue}>
          <h1>Github User Search</h1>
          <FindUser searchIs={this.handelUserSearch} />
          <ul>
            {searchedUser}
            <p>{status}</p>
          </ul>
        </ContextProvider>
      </Container>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
