import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import FindUser from "./FindUser";
import context from "./context";
import UsersLoading from "./Search";

const ContextProvider = context.Provider;
function Container(props) {
  return <div className="container">{props.children}</div>;
}
class App extends React.Component {
  state = { userName: "", UsersList: [], requestStatus: "Loading. . ." };
  userSearch = this.userSearch.bind(this);
  searchUser = this.searchUser.bind(this);

  fecthUser(query) {
    const URL = `https://api.github.com/search/users?q=${query}`;
    fetch(URL)
      .then((data) => data.json())
      .then((response) => {
        const items = response.items;

        this.setState({
          usersList: items.map((item) => {
            return item;
          }),
          requestStatus: "done",
        });
      })
      .catch((error) => {
        console.log(error);
        this.setState({ requestStatus: "Error" });
      });
  }

  userSearch(e) {
    const value = e.target.value;
    console.log(value);
    this.setState({ userName: value });
  }

  searchUser(e) {
    e.preventDefault();
    console.log(this.state.userName);
  }
  render() {
    const contextValue = {
      userSearch: this.userSearch,
      searchUser: this.searchUser,
      userName: this.state.userName,
      UsersList: this.state.UsersList,
      requestStatus: this.state.requestStatus,
    };

    return (
      <Container>
        <ContextProvider value={contextValue}>
          <h1>Github User Search</h1>
          <FindUser searchIs={this.userSearch} searchUser={this.searchUser} />
          <UsersLoading
            userName={this.userName}
            UsersList={this.UsersList}
            requestStatus={this.requestStatus}
          />
        </ContextProvider>
      </Container>
    );
  }
}

ReactDOM.render(<App />, document.getElementById("root"));
