import React from "react";
import context from "./context";
const { userName } = React.useContext(context);
const { usersList } = React.useContext(context);
const { requestStatus } = React.useContext(context);

function UsersLoding() {
  const URL = `https://api.github.com/search/users?q=${userName}`;
  fetch(URL)
    .then((data) => data.json())
    .then((response) => {
      const items = response.items;

      usersList.setState({
        list: items.map((item) => {
          return item;
        }),
        requestStatus: "done",
      });
    })
    .catch((error) => {
      console.log(error);
      requestStatus.setState({ requestStatus: "Error" });
    });

  const searchedUser = userList.map((item) => (
    <li key={item.id}>{item.login}</li>
  ));
  const status = requestStatus === "done" ? "" : requestStatus;
  return (
    <ul>
      {searchedUser}
      <p>{status}</p>
    </ul>
  );
}

export default UsersLoding;
