import React from "react";
import context from "./context";
const { userName } = React.useContext(context);
const { usersList } = React.useContext(context);
const { requestStatus } = React.useContext(context);
const query = "Mekdes1";
class UsersLoding extends React.Component {
  state = { UsersList: [], requestStatus: "Loading. . ." };
  componentDidMount() {
    const URL = `https://api.github.com/search/users?q=${query}`;
    fetch(URL)
      .then((data) => data.json())
      .then((response) => {
        const items = response.items;

        this.setState({
          list: items.map((item) => {
            return item;
          }),
          requestStatus: "done",
        });
      })
      .catch((error) => {
        console.log(error);
        requestStatus.setState({ requestStatus: "Error" });
      });
  }

  render() {
    const searchedUser = usersList.map((item) => (
      <li key={item.id}>{item.login}</li>
    ));
    const status = requestStatus === "done" ? "" : requestStatus;
    return (
      <ul>
        {searchedUser}
        <p>{status}</p>
      </ul>
    );
  }
}

export default UsersLoding;
