import React from "react";
const query = "Mekdes1";
class UsersLoding extends React.Component {
  state = { list: [] };

  componentDidMount() {
    fetch(`https://api.github.com/search/users?q=${query}`)
      .then((data) => data.json())
      .then((response) => {
        const items = response.items;

        this.setState({
          list: items.map((item) => {
            return item;
          }),
        });
      });
  }

  render() {
    const searchedUser = this.state.list.map((item) => <li>{item.login}</li>);
    console.log(searchedUser);
    return <ul>{searchedUser}</ul>;
  }
}

export default UsersLoding;
